from shapely.geometry import box
from shapely.geometry.polygon import Polygon
from descartes import PolygonPatch

fig = plt.figure(1, figsize=(5, 5), dpi=90)
ring_mixed = Polygon([(0, 0), (0, 2), (1, 1),
                      (2, 2), (2, 0), (1, 0.8), (0, 0)])
ax = fig.add_subplot(111)
